import { useState } from "react";
import styled from "styled-components";
import { login } from "../redux/apiCalls";
import { mobile } from "../responsive";
import { useDispatch, useSelector } from "react-redux";


const Container =styled.div`
    width:100vw;
    height:100vh;
    background:linear-gradient(rgba(255,255,255,0.5),rgba(255,255,255,0.5)),
    url("https://images.unsplash.com/photo-1515886657613-9f3515b0c78f?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=420&q=80") ;
    display:flex;
    background-size:cover;
    align-items:center;
    justify-content:center;
`;
const Wrapper =styled.div`
    width:25%;
    padding:20px;
    background-color:white;
    ${mobile({ width:"75%" } )}

`;
const Title =styled.h1`
    font-size:24px;
    font-weight:300;

`;
const Form =styled.form`
    display:flex;
    flex-direction:column;

`;
const Input =styled.input`
    flex: 1;
    min-width:40%;
    margin:10px 0px;
    padding:10px;
`;
const Button =styled.button`
    width:40%;
    border:none;
    padding:15px 20px;
    background-color:teal;
    color:white;
    cursor:pointer;
    margin-bottom:10px;
`;
const Link=styled.a`
    margin:10px 0px;
    font-size:12px;
    text-decoration:underline;
    cursor:pointer;
`;

const Login = () => {
  return (
    <Container>
      <Wrapper>
        <Title>SIGN IN</Title>
        <Form>
            <Input placeholder="username"/>
            <Input placeholder="password"/>
            <Button>LOGIN</Button>
            <Link>DON'T YOU REMEMBER THE PASSWORD?</Link>
            <Link>CREATE A NEW ACCOUNT</Link>
        </Form>
      </Wrapper>
    </Container>
  )
}

export default Login
